r9,r9,r8            # Adds r9 and r8        addi r11,r11,4          # increments VECX memory location by 4        addi r12,r12,4          # increments VECY memory location by 4        stw r9,0(r10)           # stores r9 at r10        bdnz Dot                # decramenting                nop                     # No Operation        nop                     # No Operation       here: b here        